void DebugFontData::loadToCanvas() {

	// フォントデータ読み込み
	for (int v = 0; v < height; v++)
	{
		for (int u = 0; u < width; u++)
		{
			ColorRGB color;
			unsigned char grey = getGreyScale256(u, v);
			grey = (grey >= 250) ? 255 : grey; // 253 以上は 255 に定義
			color.r = grey;
			color.g = grey;
			color.b = grey;
			fontCanvas.set(u, v, color);

		}
	}
}


